jQuery(document).ready(function (e) {
	"use strict";
	e(function () {
		e("#contact").validate({
			rules: {
				name: {
					required: !0,
					minlength: 2
				},
				tel: {
					required: !0,
					minlength: 10
				},
				email: {
					required: !0,
					email: !0
				},
				subject: {
					required: !0
				},
				message: {
					required: !0,
					minlength: 10
				}
			},
			messages: {
				name: {
					required: "Entrer votre nom",
					minlength: "Entrer au moins 2 caracteres"
				},
				surname: {
					required: "Entrer votre nom",
					minlength: "Entrer au moins 2 caracteres"
				},
				email: {
					required: "Cet email n'est pas valide"
				},
				subject: {
					required: "Entrer votre numéro",
					minlength: "Numéro trop court"
				},
				message: {
					required: "Entrer votre message",
					minlength: "Message trop court"
				}
			},
			submitHandler: function (r) {
				e(r).ajaxSubmit({
					type: "POST",
					data: e(r).serialize(),
					url: "php/process.php",
					success: function () {
						e("#contact :input").attr("disabled", "disabled"), e("#contact").fadeTo("slow", .15, function () {
							e(this).find(":input").attr("disabled", "disabled"), e(this).find("label").css("cursor", "default"), e("#success").fadeIn()
						})
					},
					error: function () {
						e("#contact").fadeTo("slow", 1, function () {
							e("#error").fadeIn()
						})
					}
				})
			}
		})
	})
});